package java_introduction;

public class Ternary_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;  
		int b=5;  
		boolean x=false;
		int min=(a<b)?a:b;  
		String y=(x!=false)?"Hi":"Yoh";
		System.out.println(y);		
		System.out.println(min);  
	}

}
